<script>
import jsencrypt from '@/utils/jsencrypt'
import { validUsername } from '@/utils/validate'
import '@/utils/jigsaw.min'

export default {
  name: 'Login',
  data() {
    const validateUsername = (rule, value, callback) => {
      if (!validUsername(value)) {
        callback(new Error('请输入用户名'))
      } else {
        callback()
      }
    }
    const validatePassword = (rule, value, callback) => {
      if (value.length < 6) {
        callback(new Error('密码必须大于6位'))
      } else {
        callback()
      }
    }
    return {
      showSlider: false,
      sliderValidate: false,
      loginForm: {
        username: 'visitor',
        password: '123456'
      },
      loginRules: {
        username: [{ required: true, trigger: 'blur', validator: validateUsername }],
        password: [{ required: true, trigger: 'blur', validator: validatePassword }]
      },
      loading: false,
      passwordType: 'password',
      redirect: undefined
    }
  },
  watch: {
    $route: {
      handler: function(route) {
        this.redirect = route.query && route.query.redirect
      },
      immediate: true
    }
  },
  mounted() {
    const _this = this
    window.jigsaw.init({
      el: document.getElementById('sliderBar'),
      width: 450, // 可选, 默认310
      height: 200, // 可选, 默认155
      onSuccess: function() {
        _this.sliderValidate = true
      },
      onFail: function() {
        _this.sliderValidate = false
      },
      onRefresh: function() {
        _this.sliderValidate = false
      }
    })
  },
  methods: {
    showPwd() {
      if (this.passwordType === 'password') {
        this.passwordType = ''
      } else {
        this.passwordType = 'password'
      }
      this.$nextTick(() => {
        this.$refs.password.focus()
      })
    },
    handleLogin() {
      this.showSlider = true
      if (!this.sliderValidate) {
        return
      }
      this.$refs.loginForm.validate(valid => {
        if (valid) {
          this.loading = true
          const encryptPsd = jsencrypt.encrypt(this.loginForm.password)
          this.$store.dispatch('user/login', { username: this.loginForm.username, password: encryptPsd }).then(() => {
            this.loading = false
            this.$router.push({ path: this.redirect || '/' })
          }).catch((err) => {
            console.log(err)
            this.sliderValidate = false
            this.loading = false
            document.querySelector('.jigsaw__refreshIcon--2rYeZ').click()
          })
        } else {
          console.log('error submit!!')
          return false
        }
      })
    }
  }
}
</script>

